// App.js
import React, { useState } from 'react';
import './style.module.scss'; // Stelle sicher, dass deine Styles richtig eingebunden sind.

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);

  const handleLogin = () => {
    // Hier sollte deine echte Authentifizierungslogik implementiert werden.
    // Zum Beispiel: Überprüfe die Anmeldeinformationen mit einem Server.
    // Für dieses Beispiel verwenden wir eine einfache Überprüfung.
    if (username === 'benutzer' && password === 'passwort') {
      setLoggedIn(true);
    } else {
      alert('Login fehlgeschlagen. Überprüfe Benutzername und Passwort.');
    }
  };

  const handleLogout = () => {
    setLoggedIn(false);
    // Hier könntest du weitere Aufräumarbeiten durchführen, wenn nötig.
  };

  return (
    <div className="app-container">
      {loggedIn ? (
        <div>
          <h2>Erfolgreich eingeloggt!</h2>
          <button onClick={handleLogout}>Logout</button>
        </div>
      ) : (
        <div>
          <h2>Login</h2>
          <label>
            Benutzername:
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </label>
          <br />
          <label>
            Passwort:
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </label>
          <br />
          <button onClick={handleLogin}>Login</button>
        </div>
      )}
    </div>
  );
};

export default Login;
