import React, { useState } from 'react';
import './style.module.scss';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Hier sollte deine echte Authentifizierungslogik implementiert werden.
    // Zum Beispiel: Überprüfe die Anmeldeinformationen mit einem Server.
    // Für dieses Beispiel verwenden wir eine einfache Überprüfung.
    if (email === 'user@example.com' && password === 'passwort') {
      alert('Erfolgreich eingeloggt!');
    } else {
      alert('Login fehlgeschlagen. Überprüfe Email und Passwort.');
    }
  };

  return (
    <div className="login_container">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Email:
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </label>
        <label>
          Passwort:
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </label>
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
